CREATE VIEW [v_unusable_areaexits] AS 
select ae.*, et.name, a.name
from areaexit as ae
join exittype as et on ae.exit_type_id = et.id
join area as a on ae.[area_from_id] = a.id
where ae.is_useable = 0;
CREATE VIEW [v_named_mobs] AS 
select *
from mob
where name != lower(name);
CREATE VIEW [v_areas_inc_dirt] AS 
select a.*, 1 as Derp
from area as a;
CREATE VIEW [v_areaexits_for_graph] AS 
select ae.id, ae.area_from_id, coalesce(ae.area_to_id, -1) as area_to_id, ae.exit_type_id, ae.is_useable, ae.note, ae.is_hidden
from areaexit as ae
where ae.is_useable = 1;
COMMIT;
CREATE VIEW [v_areas_for_graph] AS 
select a.* from area as a
where a.id in (
      select distinct(area_from_id)
      from v_areaexits_for_graph);
